import { Component, Input, EventEmitter, Output } from '@angular/core';
import { Product } from '../products';
import { CartService } from '../cart.service';
@Component({
  selector: 'app-product-cart-view',
  templateUrl: './product-cart-view.component.html',
  styleUrls: ['./product-cart-view.component.css']
})
export class ProductCartViewComponent {
  @Input()  product: Product | undefined
  @Output() removeEvent = new EventEmitter<number>()
  REMOVEItem(value: string) {
    this.newItemEvent.emit(value);
  }

}
<article *ngIf="product">
    <section class="product-remove">
        <button  (click)="removeEvent.emit( product.id )">Remove</button>
    </section>
    <section class="product-img">
        <img src="https://catalogo.orange.es/catalogo/Imagenes/Dispositivos/iphone_14_pro_max_morado_FrontBack.webp">
    </section>
    <section class="product-data">
        <h3>{{ product.name }}</h3> 
        <h4>{{ product.price }}</h4>
    </section>   
    <p> <dfn id="def-product">{{ product.name }}</dfn> {{ product.description }}</p>
</article>
